import React , {useEffect , useState } from "react";

import Movie from "./components/Movie";

const FEATURED_API = "https://api.themoviedb.org/3/discover/movie?api_key=211ad7c9e563f6ea964fdafbe396953a&sort_by=popularity.desc&page=1"


const SEARCHED_API = "https://api.themoviedb.org/3/search/movie?api_key=211ad7c9e563f6ea964fdafbe396953a&query="

function App() {
  const [ movies, setMovies ] = useState([]);
  const [ searchTerm,setSearchTerm] = useState('');

  useEffect(()=> {
    getMovies(FEATURED_API);
  } , []);

  const getMovies = (API) => {
    fetch(API)
    .then((res) => res.json())
    .then((data) => {
      console.log(data);
      setMovies(data.results);
    })
  }

  const handleOnSubmit = (e) => {
    e.preventDefault();

    if(searchTerm) {
      fetch(SEARCHED_API + searchTerm)
      .then((res) => res.json())
      .then((data) => {
        console.log(data);
        setMovies(data.results);
      });

      setSearchTerm("");
    }
  }

  const handleOnChange = (e) => {
      setSearchTerm(e.target.value)
  };

  return (
    <>
      <header>
        <form onSubmit={handleOnSubmit}>
          <input className="search" type="search" 
              placeholder="Search..."
              value={searchTerm}
              onChange={handleOnChange}
            />
        </form>
      </header>
      <div className="movie-container">
        {movies.length > 0 && movies.map((movie) =>
        <Movie key={movie.id} {...movie}  />)}
      </div>
    </>
  );

}

export default App;
