import React from "react";

const IMG_API = "https://api.themoviedb.org/3/movie/"
const Rimg = "images?api_key=211ad7c9e563f6ea964fdafbe396953a"

const Movie = ({title , poster_path , overview , vote_average , id}) => (
    <div className="movie">
        <img src={IMG_API + id + Rimg} alt={title} />
    </div>
);

export default Movie;
