import React , {useEffect , useState } from "react";

import Movie from "./components/Movie";

//const FEATURED_API = "https://api.themoviedb.org/3/discover/movie?api_key=211ad7c9e563f6ea964fdafbe396953a&sort_by=popularity.desc&page=1"

const GENRE_API = "https://api.themoviedb.org/3/genre/movie/list?api_key=211ad7c9e563f6ea964fdafbe396953a"

const SEARCHED_API = "https://api.themoviedb.org/3/search/movie?api_key=211ad7c9e563f6ea964fdafbe396953a&query="

function App() {
  const [ Genre , setGenre ] = useState([]);
  const [ movies, setMovies ] = useState([]);
  const [ searchTerm,setSearchTerm] = useState('');

  // useEffect(()=> {
  //   getMovies(FEATURED_API);
  // } , []);

  useEffect(()=> {
    fetch(GENRE_API)
    .then((res) => res.json())
    .then((data) => {
      console.log(data);
      setGenre(data.results);
    })
  } , []);

  const getMovies = (API) => {
    fetch(API)
    .then((res) => res.json())
    .then((data) => {
      console.log(data);
      setMovies(data.results);
    })
  }

  const handleOnSubmit = (e) => {
    e.preventDefault();

    if(searchTerm) {
      getMovies(SEARCHED_API + searchTerm)

      setSearchTerm("");
    }
  }

  const handleOnChange = (e) => {
      setSearchTerm(e.target.value)
  };

  return (
    <>
      <header>
        <form onSubmit={handleOnSubmit}>
          <input className="search" type="search" 
              placeholder="Search..."
              value={searchTerm}
              onChange={handleOnChange}
            />
        </form>
      </header>
      <div className="movie-container">
        { Genre.map((genre) => {
              const  FEATURED_API = "https://api.themoviedb.org/"+"{genre.id}"+"/genre/movie/list?api_key=211ad7c9e563f6ea964fdafbe396953a&language=en-US";
               useEffect(()=> {
                getMovies(FEATURED_API);
               } , []);
               {movies.length > 0 && movies.map((movie) =>
                <Movie key={movie.id} {...movie}  />)}
        })}
      </div>
    </>
  );

}

export default App;
